import { useState } from 'react';
import dataServices from './DataServices';
import { useNavigate, useLocation } from 'react-router-dom';


export default function Course(props) {

    const location = useLocation();
    let initialState;
    if (location.state) {
        initialState = {
            "Course Code": location.state.course['Course Code'],
            "Course Title": location.state.course['Course Title'],
            "Course Color": location.state.course['Course Color'],
            Goal: location.state.course.Goal
        };
    } else {
        initialState = {
            "Course Code": '',
            "Course Title": '',
            "Course Color": '',
            Goal: ''
        };
    }

    // console.log(JSON.stringify(location.state));
    const [state, setState] = useState(initialState);
    const navigate = useNavigate();

    // const onCreate = () => {
    //     const tempState = { ...state };
    //     tempState['courseList'][state.courseList.length] = {};
    //     tempState['isCreate'] = !tempState['isCreate'];
    //     setState(tempState);
    //     // console.log(state['courseList']);
    // }

    const onChange = (e) => {
        const tempState = { ...state };
        tempState[e.target.name] = e.target.value
        setState(tempState);
    }

    const createCourse = () => {
        // const courseObj = state.courseList[state.courseList.length - 1];
        dataServices.createCourse(props.uid, state).then((res) => {
            // setState({ ...state, result: 'course added succesfully.' });
            navigate('/courseList');
        }).catch((error) => {
            setState({ ...state, error: error.message });
        });
    }

    const editCourse = () => {
        const courseId = location.state.courseId;
        const courseObj = state;
        dataServices.updateCourseById(props.uid, courseId, courseObj).then((res) => {
            const tempState = { ...state };
            tempState['result'] = "course updated succesfully.";
            setState(tempState);
            navigate('/courseList');
            // console.log(state['courseList'][index]);
        }).catch(error => {
            setState({ ...state, error: error.message });
        });
    }

    const onColorSelect = (e, selectedColor) => {
        e.preventDefault();
        console.log(selectedColor);
        const tempState = { ...state };
        tempState['Course Color'] = selectedColor;
        setState(tempState);
        console.log(state['Course Color']);
    };

    return (
        <main className="page-auth">
            <div className="container" style={{ textAlign: 'left' }}>
                <div className="row">
                    <div className="col-lg-10 offset-lg-1">
                        <section className="auth-wrapper">
                            <div className="row">
                                <div className="col-md-6 mb-4 mb-md-0">
                                    <h2 className="auth-section-title">Add/Edit Course</h2>
                                    {/* <form action="/login.html" method="POST"> */}
                                    <form>
                                        <div className="form-group">
                                            <label className="form-group">Course Code <sup>*</sup></label>
                                            <input type="text" className="form-control" name="Course Code" value={state["Course Code"]} onChange={onChange} />
                                        </div>
                                        <div className="form-group">
                                            <label>Course Title <sup>*</sup></label>
                                            <input type="text" className="form-control" name="Course Title" value={state["Course Title"]} onChange={onChange} />
                                        </div>
                                        <div className="form-group">
                                            <label>Goal <sup>*</sup></label>
                                            <input type="text" className="form-control" name="Goal" value={state.Goal} onChange={onChange} />
                                        </div>
                                        <div className="form-group">
                                            <label>Course Color <sup>*</sup></label>
                                            {/* <input type="text" className="form-control" name="Course Color" value={state["Course Color"]} onChange={onChange} /> */}
                                            <div className="parent">
                                                <button className="round-6" style={{backgroundColor: 'pink', border: state["Course Color"] === 'pink' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, 'pink')}}></button>
                                                <button className="round-6" style={{backgroundColor: '#f66a98', border: state["Course Color"] === '#f66a98' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, '#f66a98')}}></button>
                                                <button className="round-6" style={{backgroundColor: 'red', border: state["Course Color"] === 'red' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, 'red')}}></button>
                                                <button className="round-6" style={{backgroundColor: 'orange', border: state["Course Color"] === 'orange' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, 'orange')}}></button>
                                                <button className="round-6" style={{backgroundColor: '#ffe033', border: state["Course Color"] === '#ffe033' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, '#ffe033')}}></button>
                                                <button className="round-6" style={{backgroundColor: 'lightyellow', border: state["Course Color"] === 'lightyellow' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, 'lightyellow')}}></button>
                                                <button className="round-6" style={{backgroundColor: '#cda3ff', border: state["Course Color"] === '#cda3ff' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, '#cda3ff')}}></button>
                                            </div>
                                            <div className="parent">
                                                <button className="round-6" style={{backgroundColor: '#8929ff', border: state["Course Color"] === '#8929ff' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, '#8929ff')}}></button>
                                                <button className="round-6" style={{backgroundColor: '#0300c2', border: state["Course Color"] === '#0300c2' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, '#0300c2')}}></button>
                                                <button className="round-6" style={{backgroundColor: '#2e85ff', border: state["Course Color"] === '#2e85ff' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, '#2e85ff')}}></button>
                                                <button className="round-6" style={{backgroundColor: '#75afff', border: state["Course Color"] === '#75afff' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, '#75afff')}}></button>
                                                <button className="round-6" style={{backgroundColor: '#66ffc7', border: state["Course Color"] === '#66ffc7' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, '#66ffc7')}}></button>
                                                <button className="round-6" style={{backgroundColor: '#00f504', border: state["Course Color"] === '#00f504' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, '#00f504')}}></button>
                                                <button className="round-6" style={{backgroundColor: 'green', border: state["Course Color"] === 'green' ? '2px black solid' : 'none'}} onClick={(e) => {onColorSelect(e, 'green')}}></button>
                                            </div>
                                        </div>

                                            <button className="btn btn-primary btn-auth-submit" type="button" onClick={(e) => navigate('/courseList')}>Back</button>&nbsp;
                                            {location.state ? <button className="btn btn-primary btn-auth-submit" type="button" onClick={(e) => editCourse()}>Submit</button> :
                                                <button className="btn btn-primary btn-auth-submit" type="button" onClick={(e) => createCourse()}>Submit</button>}


                                        <div>
                                            <h5 className="success-msg">{state.result}</h5>
                                            <h5 className="error-msg">{state.error}</h5>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </section>
                    </div>
                </div>
            </div>
        </main>
    )
}
