import { useEffect, useState } from 'react';
import dataServices from './DataServices';
import { useNavigate } from 'react-router-dom';
import { get } from 'firebase/database';

export default function CourseList(props) {
    const initialState = {
        uid: props.uid,
        courseList: [],
        isCreate: false,
        GPA: "N/A",
        average: "N/A",
        courseAvgList: []
    };
    const [state, setState] = useState(initialState);
    const navigate = useNavigate();

    const calculateAvg = (list) => {
        let resultarr = [];
        let sum = 0;
        let count = 0;
        let courseAvg = [];

        for (const course of list) {
            // console.log(course);
            if (course['Grade Items']) {
                const gradeList = Object.values(course['Grade Items']);
                // console.log(gradeList);  
                let courseSum = 0;
                let courseCount = 0;

                for (const gradeItem of gradeList) {
                    courseSum += gradeItem['Asmt Grade'];
                    courseCount++;
                    count++;
                    sum += gradeItem['Asmt Grade'];
                }
                courseAvg.push(courseCount === 0 ? 0 : (courseSum / courseCount).toFixed(2));
            }
        }
        return { average: (sum / count).toFixed(2), courseAvgArr: courseAvg };
    };

    const getCourseList = (uid) => {
        // e.preventDefault();
        // console.log("clicked with :" + JSON.stringify(state));


        dataServices.getCourseList(uid).then((courses) => {
            console.log('Courses: ' + JSON.stringify(courses));

            const courseListKeys = Object.keys(courses)
            const courseList = Object.values(courses);//{123: {}, id: {}} -> [{ct: 1, cd:2},{ct: 1, cd:2}]
            console.log(courseList)
            const avg = calculateAvg(courseList);
            courseList.map((course) => {
                course['isEdit'] = false;
                return course;
            });
            setState({ ...state, courseList: courseList, courseIds: courseListKeys, average: avg.average, courseAvgList: avg.courseAvgArr });
            // average, courseAvgList: avg.courseAvgArr });
            // const tempState = { ...state };
            // tempState['courseList'] = courseList;
            // tempState['courseIds'] = courseListKeys;
            // setState(tempState);

        }).catch((error) => {
            setState({ ...state, error: error.message });
            // const tempState = { ...state };
            // tempState['error'] = error.message;
            // setState(tempState);
        });
    }

    useEffect(() => { getCourseList(state.uid) }, []);

    // const onCreate = () => {
    //     const tempState = { ...state };
    //     tempState['courseList'][state.courseList.length] = {};
    //     tempState['isCreate'] = !tempState['isCreate'];
    //     setState(tempState);
    //     console.log(state['courseList']);
    // }

    const onEdit = (index) => {
        // console.log('UID :' + state.uid)
        // console.log('CourseId: ' + state.courseIds[index])
        // console.log('CourseObj: ' + state.courseList[index]['Goal'])

        navigate('/course',{state: {course: state.courseList[index], courseId: state.courseIds[index]}});

        // const tempState = { ...state };
        // tempState['courseList'][index]['isEdit'] = !tempState['courseList'][index]['isEdit'];
        // setState(tempState);
        // console.log(state['courseList'][index]);
    }

    const onChange = (e, index) => {
        const tempState = { ...state };
        tempState['courseList'][index][e.target.name] = e.target.value
        setState(tempState);
        // console.log(state['courseList']);
    }

    // const createCourse = () => {
    //     const courseObj = state.courseList[state.courseList.length - 1];
    //     dataServices.createCourse(state.uid, courseObj).then((res) => {
    //         setState({ ...state, result: 'course added succesfully.', isCreate: !state.isCreate });
    //     }).catch((error) => {
    //         setState({ ...state, error: error.message });
    //     });
    // }

    // const editCourse = (index) => {
    //     const courseId = state.courseIds[index];
    //     const courseObj = state.courseList[index];
    //     dataServices.updateCourseById(state.uid, courseId, courseObj).then((res) => {
    //         const tempState = { ...state };
    //         tempState['result'] = "course updated succesfully.";
    //         setState(tempState);
    //         console.log(state['courseList'][index]);
    //     }).catch(error => {
    //         setState({ ...state, error: error.message });
    //     });
    // }

    const deleteCourse = (index) => {
        const courseId = state.courseIds[index];
        dataServices.deleteCourseById(state.uid, courseId).then(() => {
            const tempState = { ...state };
            tempState['result'] = "course deleted succesfully.";
            tempState['courseList'].splice(index, 1);
            setState(tempState);
        }).catch(error => {
            setState({ ...state, error: error.message });
        });
    }

    return (
        <div className="container">
            <h5 className="success-msg">{state.result}</h5>
            <h5 className="error-msg">{state.error}</h5>
            {/* <h2>
                <span>Course List</span>
                <button type="button" tyle={{ float: "right" }} className="fa fa-plus btn btn-light" onClick={(e) => { onCreate(state.courseList.length); }}></button>
            </h2> */}
            <div style={{ display: 'flex', width: '100%', height: "50px", fontSize: '35px', fontWeight: '600', marginBottom: '20px' }}>
                <div style={{ width: '70%', textAlign: 'left'}}>
                    <span>Course List</span>
                </div>
                <div style={{ width: '30%', textAlign: 'right', color: 'white', fontSize: '20px' }}>
                    <button type="button" tyle={{ float: "right" }} className="fa fa-plus btn btn-light" onClick={(e) => {navigate('/course')}}></button>
                </div>
            </div>
            <div style={{ display: 'flex', width: '100%', height: "35px", fontSize: '20px', fontWeight: '600' }}>
                <div style={{ width: '70%', textAlign: 'left'}}>
                    GPA {state.GPA}
                </div>
                <div style={{ width: '30%', textAlign: 'right', color: 'white', fontSize: '20px' }}>
                    Average {state.average} %
                </div>
            </div>
            <div className='courseList'>
                <ul style={{ padding: "0" }}>
                    {state.courseList.map((function (course, index) {
                        return (
                            <li key={index} style={{ listStyle: "none", padding: '11px', background: index%2 ? 'none' : 'rgb(201 201 201 / 30%)' }} >
                                <div style={{ display: 'flex' }}>
                                    <button className="fa fa-pen btn btn-secondary" style={{ marginRight: '5px' }} type="button" onClick={(e) => { onEdit(index) }}></button>
                                    <button className="fa fa-trash btn btn-secondary" style={{ marginRight: '5px' }} type="button" onClick={(e) => { deleteCourse(index); }}></button>

                                    <div style={{ display: 'flex', width: '100%' }} onClick= {(e) => navigate('/gradeItemList')} >
                                        <div style={{ width: '5%', backgroundColor: course['Course Color'] }} ></div>
                                        <div style={{ width: '85%', textAlign: 'left', paddingLeft: '5px' }}>
                                            <div>{course['Course Code']}</div>
                                            <div>{course['Course Title']}</div>
                                        </div>
                                        <div style={{ width: '10%', textAlign: 'right', color: 'white', fontSize: '20px' }}>
                                            <div>{state.courseAvgList[index]} %</div>
                                        </div>
                                    </div>
                                </div>
                            </li>
                        );
                    }))}
                </ul>
            </div>
        </div>
    )
}
