import { useState } from 'react';
import './Signup.css';
import dataServices from './DataServices';
import { useNavigate } from 'react-router-dom';

export default function Signup(props) {
    const [state, setState] = useState({
        email: 'bhumika.rathod411@gmail.com',
        password: '',
        confirmPassword: ''
    });

    const navigate = useNavigate();

    const onChange = (e) => {
        const tempState = { ...state };
        tempState[e.target.name] = e.target.value;
        setState(tempState);
    }

    const signup = (e) => {
        e.preventDefault();
        dataServices.signUp(state.email, state.password, state.confirmPassword).then((response) => {
            setState({ ...state, result: 'Signup successfully, please login to proceed' });

            // dataServices.login(state.email, state.password).then((cred) => {
            //     setState({...state, result: 'login successfully'});
            // }).catch((error) => {
            //     setState({ ...state, result: error.message });
            // });
            // const tempState = { ...state };
            // tempState['result'] = response.message ? response.message : response;
            // setState(tempState);

        }).catch((error) => {
            setState({ ...state, result: error.message });
        });
    }

    return (
        <main className="page-auth">
            <div className="container" style={{textAlign:'left'}}>
                <div className="row">
                    <div className="col-lg-10 offset-lg-1">
                        <section className="auth-wrapper">
                            <div className="row">
                                <div className="col-md-6 mb-4 mb-md-0">
                                    <h2 className="auth-section-title">Create account</h2>
                                    <p className="auth-section-subtitle">Create your account to continue.</p>
                                    {/* <form action="/login.html" method="POST"> */}
                                    <form>
                                        <div className="form-group">
                                            <label >Email <sup>*</sup></label>
                                            <input type="email" className="form-control" name="email" placeholder="Email"
                                                value={state.email}
                                                onChange={onChange} />
                                        </div>
                                        <div className="form-group">
                                            <label >Password <sup>*</sup></label>
                                            <input type="password" className="form-control" name="password" placeholder="Password"
                                                value={state.password}
                                                onChange={onChange} />
                                        </div>
                                        <div className="form-group">
                                            <label >Confirm Password <sup>*</sup></label>
                                            <input type="password" className="form-control" name="confirmPassword" placeholder="Confirm Password"
                                                value={state.confirmPassword}
                                                onChange={onChange} />
                                        </div>
                                        <button className="btn btn-primary btn-auth-submit" type="submit" onClick={signup} >Create account</button>
                                        <div>
                                            <h6>
                                                {state.result}
                                            </h6>
                                        </div>
                                    </form>
                                    {/* <p className="mb-0">
                            <a href="login.html" className="text-dark font-weight-bold">Already have an acocunt? Sign in</a>
                        </p> */}
                                </div>
                                <div className="col-md-6 d-flex align-items-center">
                                    {/* <img src="assets/images/Register.png" alt="login" className="img-fluid" /> */}
                                </div>
                            </div>
                        </section>
                    </div>
                </div>
            </div>
        </main>
    );

}
