import { useState } from 'react';
import logo2 from "./assets/images/logo.svg";
import appleLogo from "./assets/images/apple-app-store-icon.png";
// import reviewLogo from './assets/images/app_1.png';
// import logo4 from "./assets/images/app_2.png";
import whiteLogo from "./assets/images/logo-white.svg";
import headerBG1 from "./assets/images/bg_1@2x.png";
import headerBG2 from "./assets/images/Bg_2@2x.png";
import footerBG from "./assets/images/Footer_bg.svg";
import './App.css';
// import './assets/css/style.css';
// import './assets/vendors/fontawesome-6.2.0/css/all.min.css';
import Home from './Home';
import Signup from './Signup';
import Signin from './Signin';
import CourseList from './CourseList';
import Course from "./Course";
import GradeItemList from "./GradeItemList";
import Grade from "./Grade";
import { Link, Route, Routes, useNavigate } from 'react-router-dom';
import dataServices from './DataServices';
import './assets/vendors/fontawesome-6.2.0/css/all.min.css'; 


export default function App(props) {
  const [state, setState] = useState({ 'isLoggedIn': false });
  const navigate = useNavigate();

  const signout = (e) => {
    dataServices.logOut().then(() => {
      setState({ ...state, result: "Logout Successfully logged out.", isLoggedIn: false, userName: undefined });
      navigate('/signin');
    }).catch((error) => {
      const errMsg = error.message ? error.message : error;
      console.log(errMsg);
      setState({ ...state, result: errMsg });
    });
  };

  const onSuccessfulLogIn = (userDetails) => {
    setState({ ...state, isLoggedIn: true, userName: userDetails.email, uid: userDetails.uid });
    navigate('/courseList');
  }

  return (
    <div className="gradey-header landing-header" style={{ backgroundImage: `url(${headerBG1}), url(${headerBG2})` }}>
      <header>
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-light gradey-navbar">
            <a className="navbar-brand" href="index.html">
              <img src={logo2} alt="Gradey" />
            </a>
            <button className="navbar-toggler d-lg-none" type="button" data-toggle="collapse" data-target="#collapsibleNavId" aria-controls="collapsibleNavId" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="collapsibleNavId">
              <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
                <li className="nav-item active">
                  <Link className="btn btn-secondary" to="/">Home <span className="sr-only">(current)</span></Link>&nbsp;
                  {/* <a className="nav-link" href="index.html">Home <span className="sr-only">(current)</span></a> */}
                </li>
                <li className="nav-item">
                <Link className="btn btn-secondary" to="/about">About <span className="sr-only">(current)</span></Link>
                </li>
                {/* <li className="nav-item">
                    <a className="nav-link" href="index.html#app-features">Features</a>
                  </li> */}
              </ul>
              {state.isLoggedIn ? (
                <div>
                  <label style={{ color: 'white' }}>{state.userName}</label> &nbsp;
                  <div className="nav-item mr-2 mb-3 mb-lg-0" style={{ display: 'inline' }}>
                    <button className="btn btn-primary btn-auth-signout" type="button" onClick={signout}>Sign Out</button>
                  </div>
                </div>
              ) : (
                <ul className="navbar-nav mt-2 mt-lg-0">
                  <li className="nav-item mr-2 mb-3 mb-lg-0">
                    {/* <a className="btn btn-secondary" href="register.html">Sign up</a> */}
                    <Link className="btn btn-secondary" to="/signup">Sign up</Link>
                  </li>
                  <li className="nav-item">
                    {/* <a className="btn btn-secondary" href="login.html">Login</a> */}
                    <Link className="btn btn-secondary" to="/signin">Login</Link>
                  </li>
                </ul>
              )}
            </div>
          </nav>
        </div>
      </header>
      <div style={{minHeight: '800px'}}>
        <Routes>
          <Route exact path="/" element={<Home />} />
          <Route exact path="/about" element={<div className='container' style={{padding: '10% 25% 15% 3%', display: 'flex', float:'left', textAlign: 'justify' }}>
          Lorem ipsum dolor sit amet. Sit dolores veritatis id pariatur similique et accusamus voluptatem sed recusandae reiciendis et nulla autem. Et velit dolor 33 quae consequatur et ipsum quasi.
Aut maiores molestiae et fugit reprehenderit et quidem similique cum voluptatem doloribus 33 consectetur laboriosam. Et sapiente accusamus et accusamus consequatur nihil. 
<br/><br/>33 iusto rerum et saepe enim ut atque voluptatem et error consequatur ad maxime quaerat ex quam tempore rem laborum tempora. Ab aliquam voluptatem ut placeat rerum qui illum neque adipisci tempora.
Et aliquam saepe ex reprehenderit ipsa nam earum unde. 
<br/><br/>33 incidunt impedit et voluptate rerum sed Quis quam et dolore laudantium et magni voluptatum. Sed tempore numquam non inventore voluptatum hic pariatur rerum id internos molestiae. Qui repudiandae dolorum sit itaque cumque cum fugiat quidem. 
            </div>} />
          <Route exact path="/signup" element={<Signup />} />
          <Route exact path="/signin" element={<Signin onLoggedIn={onSuccessfulLogIn} />} />
          <Route exact path="/courseList" element={<CourseList uid={state.uid} />} />
          <Route exact path="/course" element={<Course uid={state.uid} />} />
          <Route exact path="/gradeItemList" element={<GradeItemList uid={state.uid} />} />
          <Route exact path="/grade" element={<Grade uid={state.uid} />} />
        </Routes>
      </div>
      <footer className="gradey-footer text-white" style={{ backgroundImage: `url(${footerBG})` }}>
        <div className="container">
          {/* <div className="row footer-content">
            <div className="col-xl-6 col-lg-7 col-md-8">
              <h2 className="mb-0">Have a question or a feature you really want us to add? write to us.</h2>
            </div>
            <div className="col-md-4 col-lg-5 col-xl-6 py-3 py-md-0 d-md-flex align-items-center justify-content-end">
              <a href="contact.html" className="btn btn-danger btn-lg">Contact form</a>
            </div>
          </div>  */}
          <div className="row footer-widget-area">
            {/* <div className="row flex-jc"> */}
            <div className="row flex-jc">
              {/* <div className="py-3 col-md-12" style="display: flex; justify-content: center"> */}
              <div className="py-3 col-md-12 flex-jc">
                <img src={whiteLogo} alt="Gradey" padding-bottom="20px" />
              </div>
              {/* <div className="col-md-12" style="display: flex; justify-content: center; padding-bottom: 20px"> */}
              <div className="col-md-12 flex-jc">
                <a href="https://apps.apple.com/ca/app/gradey-grade-tracker/id1431015395" target="blank">
                  <img src={appleLogo} style={{ "maxWidth": "150px" }} alt="App store" />
                  {/* <img src="assets/images/apple-app-store-icon.png" style="max-width: 150px" alt="App store" /> */}
                </a>
              </div>
              {/* <div className="col-md-12" style="display: flex; justify-content: center"> */}
              <div className="col-md-12 flex-jc" >
                <p>
                  &copy; Gradey - Grade Tracker, All rights reserved.
                </p>
              </div>
            </div>
          </div>
        </div>
      </footer>
    </div >
  );
}
