import { useState } from 'react';
import './Signin.css';
import dataServices from './DataServices';

export default function Signin(props) {
    const initialState = {
        email: 'bhumika.rathod411@gmail.com',
        password: '',

    };
    const [state, setState] = useState(initialState);

    const onChange = (e) => {
        const tempState = { ...state };
        tempState[e.target.name] = e.target.value;
        setState(tempState);
    }


    const signin = (e) => {
        e.preventDefault();
        dataServices.login(state.email, state.password)
            .then((cred) => {
                setState({ ...state, result: cred.user.uid + ' user has successfully logged in'});
                props.onLoggedIn(cred.user);
            }).catch((error) => {
                const errMsg = error.message ? error.message : error;
                // const tempState = { ...state };
                // tempState['result'] = errMsg;
                // setState(tempState);
                setState({ ...state, result: errMsg});
            });
    } 

    return (
        <main className="page-auth">
            <div className="container" style={{textAlign:'left'}}>
                <div className="row">
                    <div className="col-lg-10 offset-lg-1">
                        <section className="auth-wrapper">
                            <div className="row">
                                <div className="col-md-6 mb-4 mb-md-0">
                                    <h2 className="auth-section-title">Log In</h2>
                                    <p className="auth-section-subtitle">Sign in to your account to continue.</p>
                                    <form>
                                        {/* action="/login.html" method="POST" */}
                                        <div className="form-group">
                                            <label>Email <sup>*</sup></label>
                                            <input type="email" className="form-control" name="email" placeholder="Email *"
                                                value={state.email}
                                                onChange={onChange} />
                                        </div>
                                        <div className="form-group">
                                            <label>Password <sup>*</sup></label>
                                            <input type="password" className="form-control" name="password" placeholder="Password *"
                                                value={state.password}
                                                onChange={onChange} />
                                        </div>
                                        {/* <div className="form-actions-wrapper d-flex flex-wrap align-items-center justify-content-between">
                                            <div className="form-check">
                                                <input className="form-check-input" type="checkbox" value="" />
                                                <label className="form-check-label" >
                                                    keep me login
                                                </label>
                                            </div>
                                            <a href="#!" className="forgot-password-link">Forgot password?</a>
                                        </div> */}
                                        <button className="btn btn-primary btn-auth-submit" type="submit" onClick={signin}>Submit</button>

                                        <div className='error'>
                                            <h6>
                                                {state.result}
                                            </h6>
                                        </div>
                                    </form>
                                    <p className="mb-0">
                                        {/* <a href="register.html" className="text-dark font-weight-bold">New User? Sign Up</a> */}
                                    </p>
                                </div>
                                <div className="col-md-6 d-flex align-items-center">
                                    {/* <img src="assets/images/login.png" alt="login" className="img-fluid" /> */}
                                </div>
                            </div>
                        </section>
                    </div>
                </div>
            </div>
        </main>
    );
}
