import { useEffect, useState } from 'react';
import dataServices from './DataServices';
import { useNavigate } from 'react-router-dom';

export default function GradeItemList(props) {
    const initialState = {
        uid: props.uid,
        Average: 'N/A',
        letterGrade: "N/A",
        potential: 100.0,

    };
    const [state, setState] = useState(initialState);
    const navigate = useNavigate();


    return (
        <div className="container">

            <div style={{ display: 'flex', width: '100%', height: "50px", fontSize: '35px', fontWeight: '600', marginBottom: '20px' }}>
                <div style={{ width: '70%', textAlign: 'left'}}>
                    <span>Course Name</span>
                </div>
                <div style={{ width: '30%', textAlign: 'right', color: 'white', fontSize: '20px' }}>
                    <button type="button" tyle={{ float: "right" }} className="fa fa-plus btn btn-light" onClick={(e) => {navigate('/grade')}}></button>
                </div>
            </div>
            <div style={{ display: 'flex', width: '100%', height: "35px", fontSize: '20px', fontWeight: '600' }}>
                <div style={{ width: '70%', textAlign: 'left'}}>
                    Average {state.Average}
                </div>
                <div style={{ width: '30%', textAlign: 'right', color: 'white', fontSize: '20px' }}>
                    Letter Grade {state.letterGrade} 
                </div>
                <div style={{ width: '30%', textAlign: 'right', color: 'white', fontSize: '20px' }}>
                    Potential {state.potential} %
                </div>
            </div>
            <div className='gradeItemsList'>

            </div>
        </div>
    )
}
